      var backdrops$ = $('div.powa-shot-image');

      backdrops$.each(function() {

        if (!$(this).prop('data-doppleganged')) {

          var style = $(this).attr('style');

          var matches = style.match(/url\(\"(.+)\"\)/);
          if (matches) {

            $('<img>')
                .attr('src', matches[1])
                .css({
                  'z-index': 10000,
                  width: '100%',
                })
                .addClass('show-on-print')
